<script>
export default {
	name: 'Navbar',
	data () {
		return {
		view: {
				topOfPage: true
			}
		}
	},
	beforeMount() {
		window.addEventListener('scroll', this.handleScroll)
	},
	methods: {
		handleScroll(){
			if(window.pageYOffset>370){
				if(this.view.topOfPage) this.view.topOfPage = false
			} else {
				if(!this.view.topOfPage) this.view.topOfPage = true
			}
		}
	}
}
</script>

